mapper.select:
select * from member where grade != 'admin'
and id like concat('%', #{cri.search}, '%') and name like concat('%', #{cri.search}, '%') and email like concat('%', #{cri.search}, '%')
limit #{cri.pageStart}, #{cri.perPageNum}

select * from seller where state = '승인대기'
and id like concat('%', #{cri.search}, '%') and name like concat('%', #{cri.search}, '%') and email like concat('%', #{cri.search}, '%')
limit #{cri.pageStart}, #{cri.perPageNum}

mapper.update:
update seller
	set state = #{sVo.state}, valid = #{sVo.valid}
	where id = #{sVo.id}

update coupon
	set title = #{cVo.title}, discount = #{cVo.discount}, period = #{cVo.period}, state = #{cVo.state}, valid = #{cVo.valid}
	where no = #{cVo.no}

update board
	set category = #{bVo.category}, board_no = #{bVo.board_no}, title = #{bVo.title}, contents = #{bVo.contents}, state = #{bVo.state}, views = #{bVo.views}, valid = #{bVo.valid}
	where no = #{bVo.no}

mapper.select:
select * from board where category like '%문의' and valid = 'I'
and writer like concat('%', #{cri.search}, '%') and contents like concat('%', #{cri.search}, '%')
order by no desc limit #{cri.pageStart}, #{cri.perPageNum}

select * from `comment` where category = '댓글' and valid = 'I'
and writer like concat('%', #{cri.search}, '%') and contents like concat('%', #{cri.search}, '%')
order by no desc limit #{cri.pageStart}, #{cri.perPageNum}

select * from board where category like '%답변' and valid = 'I'
and writer like concat('%', #{cri.search}, '%') and contents like concat('%', #{cri.search}, '%')
order by no desc limit #{cri.pageStart}, #{cri.perPageNum}

mapper.update:
update comment
	set valid = "D"
	where no = #{no}

update board
	set valid = "D"
	where no = #{no}
