mapper.select:
select * from board
where category like '%문의' and board_no = #{board_no}
order by no desc

select * from board
where category like '%문의' and board_no = #{board_no} and writer = #{writer}
order by no desc

select * from comment
where board_no = #{board_no}
order by no desc

mapper.update:
update board
	set state = #{state}
	where no = #{no}
