mapper.select:
select * from delivery where deliverer is null
and `start` like concat('%', #{cri.search}, '%') and `end` like concat('%', #{cri.search}, '%')
limit #{cri.pageStart}, #{cri.perPageNum}

mapper.update:
update delivery
	set company = #{dVo.company}, deliverer = #{dVo.deliverer}, phone = #{dVo.phone}, startTime = now()
	where no = #{dVo.no}

update delivery
	set endTime = now()
	where no = #{dVo.no}
