mapper.update:
update market.item
set category_no = #{iVo.category_no}, item.title = #{iVo.title}, item.file = #{iVo.file}, commend = #{iVo.commend}
where item.no = #{iVo.no}

update buy
	set request = #{bVo.request}, state = #{bVo.state}, valid = #{bVo.valid}, image = #{bVo.image}
	where no = #{bVo.no}

update wishlist
	set image = #{wVo.image}, item_name = #{wVo.item_name}, item_price = #{wVo.item_price}, valid = #{wVo.valid}
	where no = #{wVo.no}
