mapper.update:
update market.item
	set category_no = #{iVo.category_no}, title = #{iVo.title}, price = #{iVo.price}, information = #{iVo.information}, file = #{iVo.file}, commend = #{iVo.commend}, valid = #{iVo.valid}
	where no = #{iVo.no}

update buy
	set request = #{bVo.request}, state = #{bVo.state}, valid = #{bVo.valid}, image = #{bVo.image}
	where no = #{bVo.no}

update wishlist
	set image = #{wVo.image}, item_name = #{wVo.item_name}, item_price = #{wVo.item_price}, valid = #{wVo.valid}
	where no = #{wVo.no}

update couponbag
	set valid = #{cVo.valid}
	where coupon_no = #{cVo.coupon_no} and id = #{cVo.id}

update buy
	set image = #{image}
	where item_no = #{item_no}

update bag
	set image = #{image}
	where item_no = #{item_no}

update wishlist
	set image = #{image}
	where item_no = #{item_no}
